const file=document.getElementById("file"),button=document.getElementById("button");button.disabled=!0;const x2=new Image;x2.style.imageRendering="pixelated";let x1Text="",x2Text="";function ImageToBase64(e){const t=document.createElement("canvas");t.width=2*e.width,t.height=2*e.height;return t.getContext("2d").drawImage(e,0,0),t.toDataURL()}file.onchange=()=>{button.disabled=!0;const e=document.getElementById("file").files[0],t=new FileReader;t.onload=e=>{const t=e.currentTarget.result;"string"==typeof t&&(x1Text=t,x2.onload=()=>{x2Text=ImageToBase64(x2),button.disabled=!1},x2.src=t)},t.readAsDataURL(e)},button.onclick=()=>{navigator.clipboard.writeText(`document.getElementById('offline-resources-1x').src="${x1Text}";document.getElementById('offline-resources-2x').src="${x2Text}";`)};